mapper.sql:
FROM t_users t, users u

	and t_users.status =#{condition.status,jdbcType=VARCHAR}


	and t_users.name like
	concat(#{condition.name,jdbcType=VARCHAR},'%')


	and t_users.wechat_id
	=#{condition.weChatId,jdbcType=VARCHAR}


	and t_users.mobile =#{condition.mobile,jdbcType=VARCHAR}


	and
	t_users.agent_level=#{condition.agentLevel,jdbcType=VARCHAR}


	and t_users.agent_id=#{condition.agentId,jdbcType=INTEGER}


	and
	t_users.parent_agent_id=#{condition.parentAgentId,jdbcType=INTEGER}

mapper.select:
select *



		ORDER BY ${orderBy}


		ORDER BY t_users.agent_id


limit #{start},#{limit}
