mapper.sql:
from t_games_tb g,t_users u where g.id = u.userid
and g.room =#{condition.room,jdbcType=VARCHAR}
and g.id =#{condition.id,jdbcType=VARCHAR}
and u.name like concat(#{condition.name,jdbcType=VARCHAR},'%')
and g.round =#{condition.round,jdbcType=VARCHAR}
and g.game_index =#{condition.gameIndex,jdbcType=VARCHAR}
and g.is_banker =#{condition.isBanker,jdbcType=VARCHAR}
and g.create_time>=#{condition.startDate}
and g.create_time<=#{condition.endDate}
and EXISTS( SELECT * FROM ( SELECT a.userid FROM t_users a WHERE a.parent_agent_id=${agentSearchId} UNION SELECT b.userid FROM t_users b WHERE EXISTS( SELECT * FROM t_users c WHERE c.parent_agent_id=${agentSearchId} AND c.agent_id=b.parent_agent_id) UNION SELECT d.userid FROM t_users d WHERE EXISTS( SELECT * FROM t_users b WHERE EXISTS( SELECT * FROM t_users c WHERE c.parent_agent_id=${agentSearchId} AND c.agent_id=b.parent_agent_id) AND b.agent_id=d.parent_agent_id)) r where r.userid=g.id )

mapper.select:
select g.room, g.id, u.name, u.parent_agent_id, g.game_index, g.round, g.is_banker, g.base_info, g.chu, g.tian, g.mo, g.snapshots, g.coins, g.result, g.create_time
ORDER BY ${orderBy}
ORDER BY g.CREATE_TIME desc
limit #{start},#{limit}
