mapper.sql:
from t_users where 1=1

	and t_users.status =#{condition.status,jdbcType=VARCHAR}


	and t_users.name like
	concat(#{condition.name,jdbcType=VARCHAR},'%')


	and t_users.name like
	concat(#{condition.account,jdbcType=VARCHAR},'%')


	and t_users.roomId =#{condition.roomId,jdbcType=VARCHAR}


	and
	t_users.weChat_Id=#{condition.weChatId,jdbcType=VARCHAR}


	and t_users.mobile=#{condition.mobile,jdbcType=VARCHAR}

mapper.select:
select
t_users.user_id code,
t_users.name name



		ORDER BY ${orderBy}


		ORDER BY t_users.user_id


limit #{start},#{limit}

mapper.update:
update t_users
set STATUS = #{status,jdbcType=CHAR}
where userid = #{userId,jdbcType=INTEGER}

mapper.sql:
from T_USERS where 1=1

	and t_users.status =#{condition.status,jdbcType=VARCHAR}


	and t_users.name like
	concat(#{condition.name,jdbcType=VARCHAR},'%')


	and t_users.name like
	concat(#{condition.account,jdbcType=VARCHAR},'%')


	and t_users.roomId =#{condition.roomId,jdbcType=VARCHAR}


	and
	t_users.weChat_Id=#{condition.weChatId,jdbcType=VARCHAR}


	and t_users.mobile=#{condition.mobile,jdbcType=VARCHAR}

mapper.select:
select *



		ORDER BY ${orderBy}


		ORDER BY t_users.userid


limit #{start},#{limit}
