mapper.update:
update users
set STATUS = #{status,jdbcType=CHAR}
where USER_ID = #{userId,jdbcType=INTEGER}

update users
set password = #{password,jdbcType=CHAR}
where USER_ID = #{userId,jdbcType=INTEGER}

mapper.sql:
from users where 1=1
and users.name like concat(#{condition.name,jdbcType=VARCHAR},'%')
and users.department_Id =#{condition.departmentId,jdbcType=INTEGER}
and users.id_Card =#{condition.idCard,jdbcType=VARCHAR}
and users.mobile =#{condition.mobile,jdbcType=VARCHAR}
and s.area_code like concat(#{condition.areaCode,jdbcType=VARCHAR},'%')
and users.account like concat(#{condition.account,jdbcType=VARCHAR},'%')

mapper.select:
select users.user_id, users.account, users.password, users.sex, users.id_card, users.name, users.department_id, users.area_code, users.birthday, users.status, users.last_login_time, users.mobile, users.email, users.seat, users.user_level, users.create_time
ORDER BY ${orderBy}
ORDER BY users.user_id
limit #{start},#{limit}

mapper.insert:
update users_role set role_id='0' where 0=1;

